import React, { useState } from "react";

function App() {
  const [url, setUrl] = useState("");
  const [quality, setQuality] = useState("best");
  const [logs, setLogs] = useState([]);
  const [downloading, setDownloading] = useState(false);

  const startDownload = async () => {
    setLogs([]);
    setDownloading(true);
    const eventSource = new EventSource(
      `http://localhost:8080/api/youtube/download/stream?url=${encodeURIComponent(url)}&format=${quality}`
    );

    eventSource.onmessage = (e) => {
      setLogs((prev) => [...prev, e.data]);
    };

    eventSource.onerror = () => {
      eventSource.close();
      setDownloading(false);
    };
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h2>YouTube Downloader</h2>
      <input style={{ width: "300px" }} type="text" placeholder="Enter YouTube URL" value={url} onChange={(e) => setUrl(e.target.value)} />
      <select value={quality} onChange={(e) => setQuality(e.target.value)}>
        <option value="best">Best</option>
        <option value="8k">8K</option>
        <option value="4k">4K</option>
        <option value="1080p">1080p</option>
        <option value="720p">720p</option>
        <option value="480p">480p</option>
        <option value="360p">360p</option>
      </select>
      <button onClick={startDownload} disabled={downloading}>
        {downloading ? "Downloading..." : "Start Download"}
      </button>
      <pre style={{ background: "#eee", padding: "10px", marginTop: "20px", maxHeight: "300px", overflowY: "auto" }}>
        {logs.join("\n")}
      </pre>
    </div>
  );
}

export default App;
