import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [url, setUrl] = useState('');
  const [format, setFormat] = useState('720p');
  const [isDownloading, setIsDownloading] = useState(false);
  const [logLines, setLogLines] = useState([]);
  const [estimatedSize, setEstimatedSize] = useState('');

  useEffect(() => {
    if (!isDownloading) return;
    const eventSource = new EventSource(
      `http://localhost:8080/api/youtube/download/stream?url=${encodeURIComponent(url)}&format=${format}`
    );

    eventSource.onmessage = (event) => {
      const data = event.data;
      if (data.startsWith('SIZE::')) {
        const parts = data.split('::');
        setEstimatedSize(parts[2]);
      } else if (data.startsWith('PROGRESS::')) {
        setLogLines((prev) => [data, ...prev]);
      } else {
        setLogLines((prev) => [data, ...prev]);
      }
    };

    eventSource.onerror = () => {
      eventSource.close();
      setIsDownloading(false);
    };

    return () => eventSource.close();
  }, [isDownloading]);

  const handleDownload = () => {
    if (!url) return alert('Please enter a video URL.');
    setIsDownloading(true);
    setLogLines([]);
  };

  const formatOptions = [
    '8K',
    '4K',
    '1080p',
    '720p',
    '480p',
    '360p'
  ];

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <div className="max-w-xl mx-auto bg-gray-800 rounded-2xl shadow-xl p-6">
        <h1 className="text-2xl font-bold mb-4 text-center">🎬 YouTube Downloader</h1>

        <input
          type="text"
          placeholder="Enter YouTube URL"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          className="w-full p-2 rounded bg-gray-700 text-white mb-4"
        />

        <div className="flex flex-col gap-2 mb-4">
          {formatOptions.map((opt) => (
            <button
              key={opt}
              onClick={() => setFormat(opt)}
              className={`w-full rounded px-4 py-2 text-left font-semibold transition duration-150 ${
                format === opt
                  ? 'bg-blue-600 text-white'
                  : 'bg-gray-700 hover:bg-gray-600'
              }`}
            >
              {opt} {format === opt && estimatedSize && `≈ ${estimatedSize}`}
            </button>
          ))}
        </div>

        <button
          onClick={handleDownload}
          disabled={isDownloading}
          className="w-full bg-green-600 hover:bg-green-500 transition duration-150 font-semibold py-2 rounded mb-4"
        >
          ⬇️ Start Download
        </button>

        <div className="bg-gray-700 rounded p-4 space-y-2 text-sm">
          {logLines.map((line, idx) => {
            if (line.startsWith('PROGRESS::')) {
              const parts = line.split('::');
              return (
                <div key={idx} className="space-y-1 border p-3 rounded bg-gray-800">
                  <p>🔁 <span className="text-blue-400">Progress:</span> {parts[1]}%</p>
                  <p>📦 <span className="text-yellow-400">Downloaded:</span> {parts[2]}</p>
                  <p>🚀 <span className="text-green-400">Speed:</span> {parts[3]}</p>
                  <p>⏳ <span className="text-purple-400">ETA:</span> {parts[4]}</p>
                  <p>📉 <span className="text-red-400">Remaining:</span> {parts[5]}</p>
                </div>
              );
            }
            return <p key={idx}>{line}</p>;
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
