import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [url, setUrl] = useState('');
  const [quality, setQuality] = useState('1080p');
  const [progress, setProgress] = useState(null);
  const [sizeEstimate, setSizeEstimate] = useState('');
  const [downloading, setDownloading] = useState(false);
  const [messages, setMessages] = useState([]);

  const handleDownload = async () => {
    setProgress(null);
    setMessages([]);
    setDownloading(true);

    const eventSource = new EventSource(`http://localhost:8080/api/download?url=${encodeURIComponent(url)}&format=${quality}`);

    eventSource.onmessage = (e) => {
      const data = e.data;

      if (data.startsWith('SIZE::')) {
        const parts = data.split('::');
        setSizeEstimate(`${parts[1]} ≈ ${parts[2]}`);
      } else if (data.startsWith('{')) {
        try {
          const json = JSON.parse(data);
          setProgress(json);
        } catch (err) {
          console.error('Error parsing JSON:', err);
        }
      } else {
        setMessages((prev) => [...prev, data]);
        if (data.includes('Download completed')) {
          eventSource.close();
          setDownloading(false);
        }
      }
    };

    eventSource.onerror = (e) => {
      console.error('SSE error:', e);
      eventSource.close();
      setDownloading(false);
    };
  };

  return (
    <div className="app">
      <h1 className="text-xl font-bold mb-4 text-blue-600">🎬 YouTube Video Downloader</h1>

      <input
        type="text"
        placeholder="Enter YouTube URL"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        className="border p-2 rounded w-full mb-3"
      />

      <select
        value={quality}
        onChange={(e) => setQuality(e.target.value)}
        className="border p-2 rounded mb-3"
      >
        <option value="8k">8K</option>
        <option value="4k">4K</option>
        <option value="1080p">1080p</option>
        <option value="720p">720p</option>
        <option value="480p">480p</option>
        <option value="360p">360p</option>
      </select>

      {sizeEstimate && (
        <div className="text-green-700 font-medium mb-2">
          📏 Estimated Size: {sizeEstimate}
        </div>
      )}

      <button
        onClick={handleDownload}
        disabled={downloading}
        className="bg-blue-600 text-white px-4 py-2 rounded mb-4 hover:bg-blue-700 disabled:opacity-50"
      >
        {downloading ? 'Downloading...' : 'Download'}
      </button>

      {progress && (
        <div className="border border-gray-300 rounded p-4 bg-white shadow-md mb-4 space-y-2 text-sm">
          <div className="text-blue-700 font-semibold">🔁 Progress: {progress.progress}</div>
          <div className="text-purple-700">📦 Downloaded: {progress.downloaded}</div>
          <div className="text-green-700">🚀 Speed: {progress.speed}</div>
          <div className="text-yellow-600">⏳ ETA: {progress.eta}</div>
          <div className="text-red-600">📉 Remaining: {progress.remaining}</div>
        </div>
      )}

      <div className="bg-gray-100 border border-gray-300 p-2 rounded max-h-60 overflow-y-auto text-xs font-mono">
        {messages.map((msg, index) => (
          <div key={index}>{msg}</div>
        ))}
      </div>
    </div>
  );
}

export default App;
